import React, { useState } from "react";

const TeamForm = props => {
    const [card, setCard] = useState({
        name: "",
        email: "",
        role: "",
    });
    const handleChanges = e => {
        setCard({...card, [e.target.name]: e.target.value })
    };
    const getSelectValue = () => {
        const selectedValue = document.getElementById("list").value;
        setCard(card.role = selectedValue);
    }

    const submitForm = e => {
        e.preventDefault();
        props.addNewTeamCard(card);
    };
    return (
        <form onSubmit={submitForm}>
            <label htmlFor="name">Name</label>
            <input 
                id="name"
                type="text"
                name="name"
                onChange={handleChanges}
                placeholder="first and last"
                />
            <label htmlFor="email">Email</label>
            <input 
                id="email"
                type="email"
                name="email"
                onChange={handleChanges}
                />
            <label htmlFor="role">Role</label>
            <select id="list" onChange="getSelectValue();">
                <option/>
                <option value="Marketing Strategist">Marketing Strategist</option>
                <option value="Content Specialist">Content Specialist</option>
                <option value="Web Developer">Web Developer</option>
                <option value="Editor">Editor</option>
            </select> 
            <button type="submit">Submit</button>
        </form>
    )
}

export default TeamForm;
